module.exports = {
    createEdges: function(epiNodes, stromaNodes, weights) {
        var edges = [];

        for (var i = 0; i < epiNodes.length; i++) {
            for (var j = 0; j < stromaNodes.length; j++) {
                var id = "";

                var fromGene = epiNodes[i].data.id.slice(0, -2);
                var toGene = stromaNodes[j].data.id.slice(0, -2);

                if (weights[i][j] != 0) {
                    if (fromGene == toGene) {
                        id = "selfLoop"
                    } else {
                        id = "interaction"
                    }

                    edges.push({
                        data: {
                            id: id + fromGene + toGene,
                            source: epiNodes[i].data.id,
                            target: stromaNodes[j].data.id,
                            weight: weights[i][j]
                        }
                    });
                }
            }
        }

        console.log('Epi Nodes Length: ' + epiNodes.length);
        console.log('Stroma Nodes Length: ' + stromaNodes.length);
        console.log("Edges Length: " + edges.length);

        return edges;
    },
    createEdgesFromNode: function(node, neighbours, weights) {
        var edges = [];
        var idPrefix = node.data.id.endsWith('-E') ? "EpiToStroma" : "StromaToEpi";
        for (var i = 0; i < neighbours.length; i++) {
            if (node.data.id)
                edges.push({
                    data: {
                        id: idPrefix + neighbours[i].data.id.slice(0, -2),
                        source: node.data.id,
                        target: neighbours[i].data.id,
                        weight: weights[i]
                    }
                })
        }

        return edges;
    },

    createEdgesFromREdges: function(REdges) {
        var edges = [];

        for (var i = 0; i < REdges.length; i++) {
            var source = REdges[i].value[0].value[0];
            var target = REdges[i].value[1].value[0];
            var weight = REdges[i].value[2].value[0];
            edges.push({
                data: {
                    id: source + target,
                    source: source,
                    target: target,
                    weight: weight
                }
            });
        }

        return edges;
    }
};
